import React, { useContext, useState } from 'react';
import { AuthContext } from '../../contexts/AuthContext';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const { isAuthenticated } = useContext(AuthContext);

  const [newTask, setNewTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleAddTask = () => {
    if (newTask.trim()) {
      const taskObj = {
        title: newTask,
        status: 'Not Started',
        hours: 0
      };
      setTasks([...tasks, taskObj]);
      setNewTask('');
    }
  }

  const handleStatusChange = (index, status) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].status = status;
    setTasks(updatedTasks);
  }

  const handleHoursChange = (index, hours) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].hours = hours;
    setTasks(updatedTasks);
  }

  const handleFormSubmit = () => {
    // Send tasks data to the database
    // You can use an API request here to send the data to your server

    // Reset the tasks once submitted
    setTasks([]);
  }

  return (
    <div className="dashboard-container">
      <h2>Employee Dashboard</h2>

      <div className="task-input">
        <input
          type="text"
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
          placeholder="Enter new task"
        />
        <button onClick={handleAddTask}>Add Task</button>
      </div>

      <table className="tasks-table">
        <thead>
          <tr>
            <th>Task Title</th>
            <th>Status</th>
            <th>Hours Spent</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task, index) => (
            <tr key={index}>
              <td>{task.title}</td>
              <td>
                <select
                  value={task.status}
                  onChange={e => handleStatusChange(index, e.target.value)}
                >
                  <option value="Not Started">Not Started</option>
                  <option value="In Progress">In Progress</option>
                  <option value="Done">Done</option>
                </select>
              </td>
              <td>
                <input
                  type="number"
                  value={task.hours}
                  onChange={e => handleHoursChange(index, e.target.valueAsNumber)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={handleFormSubmit}>Submit Tasks</button>
    </div>
  );
}

export default EmployeeDashboard;
