import React, { useContext, useState } from 'react';
import { AuthContext } from '../../contexts/AuthContext';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const { isAuthenticated } = useContext(AuthContext);

  const [newTask, setNewTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleAddTask = () => {
    if (newTask.trim()) {
      const taskObj = {
        title: newTask,
        status: 'Not Started',
        hours: 0,
      };
      setTasks([...tasks, taskObj]);
      setNewTask('');
    }
  }

  const handleStatusChange = (index, status) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].status = status;
    setTasks(updatedTasks);
  }

  const handleHoursChange = (index, hours) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].hours = hours;
    setTasks(updatedTasks);
  }

  const handleDataSubmission = () => {
    // Prepare the data to send to the server (replace with actual data)
    const dataToSubmit = {
      title: 'Task Title',
      status: 'Not Started',
      hours: 0,
    };

    // Send a POST request to the server
    fetch('http://localhost/workwave/employeedashboard.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(dataToSubmit),
    })
      .then(response => response.text())
      .then(data => {
        // Handle the response from the server (e.g., show a success message)
        console.log(data);
      })
      .catch(error => {
        // Handle any errors
        console.error(error);
      });
  }


  return (
    <div className="dashboard-container">
      <h2>Employee Dashboard</h2>

      <div className="task-input">
        <input
          type="text"
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
          placeholder="Enter new task"
        />
        <button onClick={handleAddTask}>Add Task</button>
      </div>

      <div className="tasks-grid">
        {tasks.map((task, index) => (
          <div key={index} className="task-card">
            <span className="task-title">{task.title}</span>
            <select value={task.status} onChange={e => handleStatusChange(index, e.target.value)}>
              <option value="Not Started">Not Started</option>
              <option value="In Progress">In Progress</option>
              <option value="Done">Done</option>
            </select>
            <input
              type="number"
              value={task.hours}
              onChange={e => handleHoursChange(index, e.target.valueAsNumber)}
              placeholder="Add hours spent"
            />
          </div>
        ))}
      </div>

      <button className="submit-button" onClick={handleDataSubmission}>Submit</button>
    </div>
  );
}

export default EmployeeDashboard;
